var r = require ('refs');
var q = new r.Refs('a/.git', function(err) {
	if (err) {
		console.log(err);
	}
	q.createTag('pop', '1d6011d225dd48a3685607ab55ca48bb02881b8b', function(err) {
		q.getTagsNames(function(err, tags) {
		if (err) {
			console.log(err);
			return;
		}
		console.log(tags);
	});
	});
});