var rem = require('gitRemoteCommand');

rem.getRemotesNames('.git', function(err, names) {
	if (err) {
		console.log(err);
		return;
	}
	console.log(names);
	return;
});

rem.getRemoteBranchDetails('.git', 'origin', 'refs_remote_manager', function(err, det) {
	if (err) {
		console.log(err);
		return;
	}
	console.log(det);
	return;
});