rem.addNewRemote('.git', 'pop', function(err) {
	if (err) {
		console.log(err);
		return;
	}
	var refs = new rM.Refs('.git', function(err) {
		if (err) {
			console.log(err);
			return;
		}
		refs.setBranchInRemote('posp', 'lka', 'test', function(err) {
			if (err) {
				console.log(err);
				return;
			}
		});
	});
});